(38, 'Fiestas de la Fundación de Loreto.', 'fiestas_de_la_fundacion_de_loreto_', NULL, NULL, NULL, 'Es uno de los eventos culturales más importantes en el Estado.', NULL, NULL, NULL, '528c58f2f26c40d5833b1689d24aa343.jpg', 7, '', '', '', '', 0, '', '', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(39, 'Fiestas de San Javier.', 'fiestas_de_san_javier_', NULL, NULL, NULL, 'Las festividades se celebran en honor de San Francisco Javier, santo patrón del vecino pueblo de San Javier. Estas fiestas atraen numerosos peregrinos de toda la península.', NULL, NULL, NULL, 'e0626a0986d81de5726d1cbbba954138.jpg', 7, '', '', '', '', 0, '', '', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(40, 'Loreto 400. ', 'loreto_400_', NULL, NULL, NULL, 'Es una clásica carrera off road en circuito desértico, cuya ruta incluye los pueblos de Comondú, San Javier y comunidades rurales de la Sierra de la Giganta.', NULL, NULL, NULL, '9dba01a0a19984b5089650f2a8d966de.jpg', 7, '', '', '', '', 0, '', '', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
