INSERT INTO `vpm_medioambiente` (`idMedioAmbiente`, `nombreAmbiente`, `descripcionMedioAmbiente`, `imgMedioAmbiente`, `tipo`, `direccionMedioAmbiente`, `latitudMedioAmbiente`, `longitudMedioAmbiente`, `idUsuario`) VALUES
(1, 'Ardilla', 'La ardilla es un mamífero roedor. Unas 200 especies de ardillas viven en todo el mundo, con excepción de Australia.', 'f0adfe4707f151c218d6fdebeae8aaca.jpg', 'Fauna', '.', '', '', 10),
(2, 'Armadillo', 'El armadillo es un mamífero terrestre. Su cola de reptil, sus orejas de mula y su caparazón lo hace un animal muy particular.', 'd3d2ce68113fac2175bf9a832337a0b8.jpg', 'Fauna', '', '', '', 10),
(3, 'Conejo', 'El conejo es un mamífero. Tiene un cuerpo cubierto de un pelaje espeso y lanudo, de color pardo pálido a gris, cabeza ovalada y ojos grandes.', '20160eb189e4a0394b64b309f6a32115.jpg', 'Fauna', '.', '', '', 10),
(4, 'Coyote', 'El coyote una especie de mamífero carnívoro de la familia Canidae. Su comportamiento puede variar mucho según su hábitat, pero en general viven y cazan solos o en parejas monógamas.', 'fa613cc927c2d495fb52c3c731420ceb.jpg', 'Fauna', '.', '', '', 10),
(5, 'Gato Montés', 'El gato montés es un felino salvaje que habita en los bosques, zonas de matorral  y semidesiertos de Norteamérica, desde el sur de Canadá hasta el sur de México.', '480d81f5450b42bef6fa841ccfbb37bf.jpg', 'Fauna', '.', '', '', 10),
(6, 'Mapache', 'El mapache es pequeño, de pelo medianamente largo y de color gris plateado, más oscuro en el centro del lomo, cola larga y anillada y una característica mancha de pelo negro en cada ojo.', '85dc623d676e4480ffe4e043f06838ee.jpg', 'Fauna', '.', '', '', 10),
(7, 'Liebre', 'Una liebre es un mamífero muy veloz, tímido y solitario, y vive en las llanuras sin hacer madrigueras; es comestible y muy estimado como pieza de caza.', 'fc034146ad3e25e32b825e5025134814.jpg', 'Fauna', '.', '', '', 10),
(8, 'Lirón', 'El lirón es una especie de roedor esciuromorfo de la familia Gliridae. Es una de las especies menores más características del panorama faunístico español por estar presente en todo el territorio con gran densidad de población.', '6079ec62feaf40291e0d1ffe5447ed41.jpg', 'Fauna', '.', '', '', 10),
(9, 'Zorrillo', 'Son una familia de mamíferos carnívoros conocidos vulgarmente como mofetas, zorrinos, zorrillos, mapurites o chingues. Son animales de mediano tamaño, que habitan principalmente en América, con un género en el Sudeste Asiático.', '5ac70eaa7e1f927d2a9011f73cf7ab92.jpg', 'Fauna', '.', '', '', 10),
(10, 'Tlacuache', 'El tlacuache es un animal omnívoro que no ha sufrido variaciones desde que apareció en nuestro planeta hace aproximadamente sesenta millones de años. Se alimenta de cualquier cosa por lo que acostumbra estar la mayor parte del tiempo cerca del hombre.', '8895bf5f218deba86515e0fedc19729a.jpg', 'Fauna', '.', '', '', 10),
(11, 'Águila', 'El águila es un ave rapaz diurna de hasta 1 m de altura, plumaje, alas de gran envergadura, pico fuerte y curvado en la punta, vista muy aguda y vuelo rapidísimo; se alimenta de otros animales, a los que apresa con sus fuertes garras.', 'eec6e1bb23b51cf2e02b94a5c08754a6.jpg', 'Fauna', '.', '', '', 10),
(12, 'Codorniz', 'La codorniz es una especie de ave galliforme de la familia Phasianidae. Es un ave pequeña y rechoncha aunque con alas largas, que le permiten volar largas distancias adaptadas a su vida nómada y a sus migraciones entre continentes.', '10feec54d0516694808c93f11aed48e1.jpg', 'Fauna', '.', '', '', 10),
(13, 'Paloma', 'La paloma pertenece a la familia de aves del orden Columbiformes. Se distribuyen por todo el mundo, excepto la Antártida y el Ártico, y con centro de dispersión en América Central.', 'a16f02995ed42907f82414a2a91624a4.jpg', 'Fauna', '.', '', '', 10),
(14, 'Zopilote', 'El zopilote es la única especie del género Coragyps. Es un ave accipitriforme, aunque algunas clasificaciones lo incluyen en el orden Ciconiiformes, y uno de los más abundantes miembros de la familia de los buitres del Nuevo Mundo.', '00ab82612d04652c4bd31fe3a6b2cfe6.jpg', 'Fauna', '.', '', '', 10),
(15, 'Serpientes', 'Las serpientes son un suborden de saurópsidos o reptiles diápsidos pertenecientes al orden Squamata, superorden de los Lepidosaurios, caracterizado por la ausencia de patas 1 y el cuerpo muy alargado.', '59dbec6c921ef3469d66b8e8924b4b89.jpg', 'Fauna', '.', '', '', 10),
(16, 'Centro de Educacion Ambiental Barranca de Doguithi', '', 'cf187b26d363e40ad77424ec527c2f99.jpg', 'UMA', '', '', '', 10),
(17, 'UMA de Vida Silvestre y Ecoturismo Comodeje', '', 'c0739786832ad710da6e8205185565e7.jpg', 'UMA', '', '', '', 10),
(18, 'UMA de Vida Silvestre y Ecoturismo del Ejido', '', 'bc16108d29d22612014a6cf12933f2ec.jpg', 'UMA', '', '', '', 10),
(19, 'UMA de Vida Silvestre y Ecoturismo Zequeteje', '', '4ca4ca8c25a47dee8962304af874d371.jpg', 'UMA', '', '', '', 10),
(20, 'Árboles de Pirúl', 'Árbol de hasta 15 m de alto, con tronco robusto, muy ramificado y corteza marrón oscuro, rugosa, agrietada. Copa con forma de paraguas y follaje permanente. ', 'fce3c4618870ce878d3dc70ae7cb867b.jpg', 'Flora', '.', '', '', 10),
(21, 'Encino Prieto', 'Es una especie arbórea perteneciente a la familia de las Fagáceas. Está clasificada en la Sección Lobatae; tienen los estilos largos, las bellotas maduran en 18 meses y tienen un sabor muy amargo.', 'a695dac69bdd8cff1180851bcdda13d2.jpg', 'Flora', '.', '', '', 10),
(22, 'Garambullo', 'Es endémica de Hidalgo, Querétaro, Oaxaca, San Luis Potosí, Guanajuato, Puebla y Tamaulipas en México. Es una especie muy común que se ha extendido por todo el mundo.', 'c19280f2c1a0645e0368206686b0712d.jpg', 'Flora', '.', '', '', 10),
(23, 'Maguey', 'Su centro de origen está es una planta verde y morada en México –los grupos humanos originarios de esta región aprovecharon esta planta desde hace por lo menos diez mil años; usados por sus fibras o por el aguamiel.', 'a74db80b2edbb5a6742e320b5226107f.jpg', 'Flora', '.', '', '', 10),
(24, 'Oyamel', 'Árbol con copa cónica, hasta 35 metros de altura, la corteza gruesa y oscura, rugosa cuando viejo. Las hojas son angostas y pequeñas, planas más largas que anchas, se encuentran insertadas de forma de espiral.', 'eb994d19b2143e2bb3039c4bdaa2ea8b.jpg', 'Flora', '.', '', '', 10),
(25, 'Palma Yuca', 'Es un miembro de la familia de los agaves. Esta planta en forma de árbol produce pequeños pero atractivos racimos de hojas y flores. Es una buena planta de paisajismo en sitios calurosos y secos, donde las heladas de invierno son poco frecuentes.', '197ba90ce1f30d8cc9c5a825612f9910.jpg', 'Flora', '.', '', '', 10),
(26, 'Coyote', 'El coyote una especie de mamífero carnívoro de la familia Canidae. Su comportamiento puede variar mucho según su hábitat, pero en general viven y cazan solos o en parejas monógamas.', '6b521f82d9519f517614d600494ab9b3.jpg', 'Fauna', '.', '', '', 235),
(27, 'Ardilla', 'La ardilla es un mamífero roedor. Unas 200 especies de ardillas viven en todo el mundo, con excepción de Australia.', 'e2ab6202754cc28449c6bbdf1c16d13f.jpg', 'Fauna', '', '', '', 235),
(28, 'Jabalí', 'El Jabalí es un mamífero artiodáctilo de la familia de los suidos presente en Europa, es de tamaño mediano provisto de una cabeza grande y alargada, en la que destacan unos ojos muy pequeños.', 'c258e23b3dd2170ed09666cec40fe7cb.jpg', 'Fauna', '.', '', '', 235),
(29, 'Jaguar', 'El Jaguar es un carnívoro félido de la subfamilia de los Panterinos y género Panthera y la única de las cuatro especies actuales de este género que se encuentra en América.', 'de8d006872d2aef62e9dc592e73708ff.jpg', 'Fauna', '', '', '', 235),
(30, 'Tejón', 'El tejón es un mustélido nativo del Viejo Mundo, de tamaño medio, que puede llegar a pesar unos 15 kg. Tiene la cabeza alargada y triangular, con el cuerpo también alargado de hasta 90 cm de longitud de los que 15 a 20 cm corresponden a la cola.', 'beab04c08793c312acf04078cec29bb2.jpg', 'Fauna', '', '', '', 235),
(31, 'Puercoespín', 'Puerco espín, roedor herbívoro cuya característica más notable son los pelos que recubren su cuerpo modificados en espinas y en púas situadas a lo largo del dorso y en los laterales del cuerpo o, como ocurre en algunas especies, presentes desde la cabeza hasta la cola.', '1580e7032eeb45770592885012267716.jpg', 'Fauna', '.', '', '', 235),
(32, 'Nutria', 'Las nutrias son adorables criaturas que viven tanto en la tierra como en el agua y a menudo se les confunde con los castores. Viven en tierras cerca de cuerpos de agua, y cazan su comida tanto en el agua, incluyendo a los peces,  como en tierra, incluidas las aves.', '335f7fdb89ffa67c868f9004a9d88bc6.jpg', 'Fauna', '', '', '', 235),
(33, 'Cenzontle', 'Cenzontle es un ave paseriforme nativa de América del Norte, América Central y parte de América del Sur. Los ejemplares adultos son grises por la parte superior del cuerpo, con ojos de un amarillo pálido y pico negro mínimamente curvo.', '3c2203e477d87472d677eadc2b84bc6a.jpg', 'Fauna', '', '', '', 235),
(34, 'Jilguero', 'El Jilguero es un ave paseriforme perteneciente a la familia de los fringílidos. Es un ave granívora, alimentándose de semillas de girasol, trigo, algunos insectos en la estación de cría y sobre todo de las semillas de los cardos.', '0e3ba30b1189c9ac457e28acdcec12de.jpg', 'Fauna', '', '', '', 235),
(35, 'El Canchán', 'El Canchán, también llamada "rosada" por el color de su piel. Sirve muy bien para el locro o la huatia, y es apropiada para preparar la papa rellena. Esta variedad es resistente a la rancha y está adaptada a las condiciones de la Sierra Central.', '13a04947167ae7d47ecbe3b0b5c5ef0b.jpg', 'Flora', '', '', '', 235),
(36, 'Ojoh', 'Ojoh crece a partir de un bulbo que surge en el suelo. Si el bulbo de color verde, no se le ataca, comenzará a enraizarse en el suelo. Una vez enraizado, comenzarán a salir estas plantas ojo, una tras otra invadiendo todo el terreno y comiendo conejos, pájaros, mariposas o abejas.', '09d16cdb471959cd046ee888ce5cb54f.jpg', 'Flora', '.', '', '', 235),
(37, 'Palo de Agua', 'El Palo de Agua es un tubo largo y hueco rellenado con piedrecillas, semillas, arroz o arena en cuyo interior se clavan palitos de bambú o de madera, formando una espiral que se extiende a todo lo largo. ', '917618d97036d197e868e50a1b1737eb.jpg', 'Flora', '', '', '', 235),
(38, 'Palo Mulato', 'El Palo Mulato es un árbol pequeño a mediano, de hasta 25 m de altura y de 10 a 100 cm de diámetro, con tronco cilíndrico ramificado de baja a mediana altura y copa irregular y dispersa. ', '9967ae712eb79753cdb06cb01c901837.jpg', 'Flora', '', '', '', 235),
(39, 'Encino', 'Los encinos son árboles o arbustos que viven en las partes bajas de las montañas en donde forman bosques de varias especies de encinos o mezclados con varias especies de pinos.', '211f12bd821a268782eb2f8183e2419a.jpg', 'Flora', '', '', '', 235),
(40, 'Oyamel', 'Oyamel es un árbol de tamaño grande, perennifolio, de 25 a 50 m de altura, con un tronco recto de hasta 2 m de diámetro. Las hojas son como agujas, chatas, de 15 a 35 mm de longitud y 1,5 mm de ancho por 0,5 mm de espesor.', 'a462718c55a78810ce2316a7232cfd93.jpg', 'Flora', '', '', '', 235),
(41, 'Río Coacoyunga', 'Si se llega a Chignahuapan por la Carretera federal (119) deberá cruzar el Río Coacoyunga, sus aguas se integran a hermosos paisajes naturales antes y después del Balneario de aguas termales.', '200b3c7dd69e82e3bc42e01568c8b07e.jpg', 'UMA', '.', '', '', 277),
(42, 'Presa de Cuautelolulco', 'La Presa de Cuautelolulco hace honor al nombre del municipio. Rodeada de bosques, llanos y tierras de labor, ofrece magníficas vistas del paisaje natural en que se encuentra, los innumerables rebaños de ovejas que llegan a pastar alrededor de la presa.', 'acbe4c92a8816457e6623196b3ac3747.jpg', 'UMA', '.', '', '', 277),
(43, 'Llano del Conejo', 'Al costado oriente de la Carretera Federal No. 119, en el inicio de la Sierra Norte Poblana, se ubica este llano rodeado de bosques de pino y encino, lo cruza un pequeño riachuelo. Su acceso es muy sencillo pues se encuentra a pie de carretera.', '29ac9a3c7c55f94063bb0155220a3d28.jpg', 'UMA', '', '', '', 277),
(45, 'Encinos', 'Los encinos son materia prima básica para muchos procesos productivos, a nivel mundial, la madera de los encinos es considerada de alta calidad. También son importantes proveedores de servicios ambientales.', 'f124cd5f4508f507e12f739fde86d2e4.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(46, '  Conejo', 'El conejo es un mamífero. Tiene un cuerpo cubierto de un pelaje espeso y lanudo, de color pardo pálido a gris, cabeza ovalada y ojos grandes.', '0799c1a51207f2e3c6a0edf5f965a444.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(47, '  Ardilla', 'La ardilla es un mamífero roedor. Unas 200 especies de ardillas viven en todo el mundo, con excepción de Australia.', 'd8275535e6272e46ccc92575993fb1d2.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(48, 'Codorniz', 'La codorniz es una especie de ave galliforme de la familia Phasianidae. Es un ave pequeña y rechoncha aunque con alas largas, que le permiten volar largas distancias adaptadas a su vida nómada y a sus migraciones entre continentes.', 'f5808b2e9d9d299d7701a3d2b2b32b78.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(49, '  Águila', 'El águila es un ave rapaz diurna de hasta 1 m de altura, plumaje, alas de gran envergadura, pico fuerte y curvado en la punta, vista muy aguda y vuelo rapidísimo; se alimenta de otros animales, a los que apresa con sus fuertes garras.', 'd18b219c21e931a4142cedb9af17d034.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(50, '  Liebre', 'Una liebre es un mamífero muy veloz, tímido y solitario, y vive en las llanuras sin hacer madrigueras; es comestible y muy estimado como pieza de caza.', '8f7338edfec8119e852dc3f70452b8ef.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(51, 'Zopilote', 'El zopilote es la única especie del género Coragyps. Es un ave accipitriforme, aunque algunas clasificaciones lo incluyen en el orden Ciconiiformes, y uno de los más abundantes miembros de la familia de los buitres del Nuevo Mundo.', '7320c80db48eee1150f3e6f5fbbb8ee0.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(52, 'Tejón', 'El tejón tiene complexión robusta, cuerpo mucho más largo y ancho que alto, con patas cortas y cola también corta con el extremo romo. Su forma general es algo acuñada, con la cabeza alargada y triangular y proporcionalmente pequeña. El cuerpo también es alargado.', 'a27d7d4d77aa0f3a254f5a74b52b364a.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(53, 'Halcón', 'Los halcones son aves rapaces. Las patas terminan en garras largas y fuertes. El pico es relativamente corto, grueso y curvado hacia abajo. Las patas las usan para atrapar, algunas veces al vuelo, y el pico para desgarrar a sus presas.', '6d1856d10dd538befa8496f9e871b612.png', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(54, 'Gavilán ', 'El gavilán es una rapaz diurna de pequeño tamaño. Su cabeza es corta y pequeña.', '3c1f8274cefe78dd80005d0304a04e94.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(55, 'Venado', 'Los venados o ciervos son mamíferos rumiantes que pertenecen a la familia Cervidae.Todos los venados machos tienen cuernos, menos el ciervo acuático chino, que tiene colmillos.', 'd365def084d58e405d9bb6f5ed702077.jpg', 'Fauna', 'San Pedro de los Pozos, Gto.', '', '', 35),
(57, 'Pino', 'Pertenecientes al grupo de las coníferas y, dentro de éste, a la familia de las pináceas, que presentan una ramificación frecuentemente verticilada y más o menos regular.', 'c6fb01c39921ea71a1a59f90b905b8cd.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(58, 'Nopalera', 'Constituye, en una escala regional, un universo de especies vegetales y animales que sólo se hallan bajo esas condiciones particulares. Los factores naturales, así como el uso que se da a las nopaleras silvestres determinan el número de especies de nopal y su abundancia.', 'b6f0e643fedd2894079683808c01d2ef.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(59, 'Navajita', 'Es una especie de planta perenne herbácea perteneciente a la familia de la poáceas representa la mayor parte de la productividad primaria neta en la pradera de pastos cortos de las Grandes Llanuras centrales y meridionales.', 'f6bb1ae520136c6679c334e92e51cb4d.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(60, 'Tigridia', 'Las especies de Tigridia flor de tigre presentan grandes y atractivas flores que no duran más que un día, florece de septiembre a noviembre a finales o mediados de la temporada lluviosa. Si bien varias flores pueden abrir sucesivamente en el mismo escapo.', '5abcaaaaf458297b3c0b07a9c8e58157.JPG', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(61, 'Mezquite', 'Estos árboles caducifolios generalmente llegan a medir entre 6 y 9 m de altura, aunque es común encontrarlos como arbustos. Tienen hojas angostas, bipinnadas, compuestas, de 5 a 7,5 cm de largo y con puntas suaves, y ramas con espinas.', 'd55eb02db5e7906cb1ded483f8505121.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(62, 'Liendrilla ', 'Es una maleza anual, de climas templados, crece en cultivos de cereales, es muy competitiva con el cultivo cuando no hay malezas de hoja ancha.', '1e61a1d118a69e17b4b5c267882cfe13.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(63, 'Tempranero', 'Es una especie de pasto que alcanza un tamaño de 10 a 99 cm de altura. Aunque buen forraje para el ganado, solo se utiliza en la vida silvestre. Se reproduce por semillas y tallos.', '983b06bdce60a86e31309ba0697077e1.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(64, 'Palma China', 'Alcanza hasta más de 10 m de altura, muy ramificada hasta con 40 ramas. Hojas hasta de 55 cm × 3,6 cm; lineares oblanceoladas, constreñidas cerca de la base, rígidas, generalmente ásperas en ambas superficies.', '9d4a96d8a9653f20ea9cd1262a4b0855.JPG', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(65, 'Tres barbas', 'Se distingue por tener lemas muy alargadas que terminan en 3 aristas. En algunas especies las  aristas laterales son reducidas y no se ven fácilmente.', '20ba8cac12f768bccf8ddfc0549d2846.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(66, 'Huisache ', 'Arbusto espinoso o árbol pequeño, perennifolio o subcaducifolio, de 1 a 2 m de altura la forma arbustiva y de 3 a 10 m la forma arbórea, con un diámetro a la altura del pecho de hasta 40 cm.', '0477534d1c5c3e1c5b0572b99094d2a3.JPG', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(67, 'Gatuño', 'Es un arbusto de que varía entre el medio y los tres metros de altura, las ramas jóvenes son estriadas o acostilladas; las ramas maduras tienden a ser rollizas, armadas con aguijones infraestipulares solitarios.', 'e9bcab2f62e7c230ffcf8f75dfecf251.jpg', 'Flora', 'San Pedro de los Pozos, Gto.', '', '', 35),
(70, 'Bahamensis.', 'Es una especie de árbol en la familia de las fabáceas. Es originaria de América.', '04259ff4b91d7e8ab5b77861d9da82d4.jpg', 'Flora', '', '', '', 76),
(71, 'Bosques de pino.', 'Este tipo de bosque se puede encontrar sobre distintos tipos de roca: ígneas, metamórficas y sedimentarias; mientras que los suelos sobre los que se asienta son delgados y poco desarrollados, siendo litosol, regosol y cambisol los principales.', 'ce0b9c47e31aa45d331086ea0b4a8644.jpg', 'Flora', '', '', '', 76),
(72, 'Lysiloma.', 'Son árboles, que alcanzan un tamaño de 3–15 (–20) m de alto, corteza grisácea, escamosa, ramas glabras a esparcidamente canescente-tomentulosas. ', 'aefad3f173e984e98d2945df2fbb3282.jpg', 'Flora', '', '', '', 76),
(73, 'Aguila.', 'Las diversas especies y subespecies de águilas pueden encontrarse en casi cualquier parte del mundo excepto en la Antártida. Son miembros de las aves de presa, del orden de Accipitriformes.', 'ef3a065ab096396db55c4831a94685e9.jpg', 'Fauna', '', '', '', 76),
(74, 'Venado.', 'Los venados o ciervos son mamíferos rumiantes que pertenecen a la familia Cervidae. Esta clasificación incluye a animales como el alce, el ciervo rojo, el reno, el corzo y el axis, entre otros. Todos los venados machos tienen cuernos, menos el ciervo acuático chino, que tiene colmillos.', '9d86a089cfe0c3330d08934fe35e9d17.jpg', 'Fauna', '', '', '', 76),
(75, 'Conejo.', 'Se caracteriza por tener un cuerpo cubierto de un pelaje espeso y lanudo, de color pardo pálido a gris, cabeza ovalada y ojos grandes.', '523d483ada6f7c25ada92b3b9764a7da.jpg', 'Fauna', '', '', '', 76),
(76, 'Ceiba o pochota', 'Ceiba pentandra). Ya-axché, como le llaman en la cultura maya, donde se dice que el universo está estructurado en tres planos: el celeste, el terrestre y el inframundo, los cuales se comunican a través de la ceiba sagrada. Las ramas de este árbol permiten la apertura de los trece cielos. En Comitán, las pochotas más representativas se encuentran en el parque de San Sebastián y en el parque de La Pila.', '7f0b45196df6bba4d9024ec65dfcc369.JPG', 'Flora', '', '', '', 100),
(77, 'Ciprés comiteco', '(juniperus comitana), especie endémica que solo se encuentra en el municipio de Comitán, Chiapas y parte de Guatemala.', 'c849c73cb6bc9ea8c72fb2856a96281c.jpg', 'Flora', '', '', '', 100),
(78, 'Jaboncillo', 'Mejor conocido como chío. Es un árbol de flores amarillentas y fruto carnoso y amargo que produce una especie de espuma al ser frotado. La semilla de esta fruta es esférica, dura y de color negruzco. En los años cincuenta y sesenta, los niños de bajos recursos, la usaban para jugar canicas. En el parque central de Comitán hay un árbol de chío que ha servido como referencia para muchas generaciones. La cronista de Comitán, doña Lolita Albores, con frecuencia hacía mención de este árbol.', '51b647a1d7d87fc28e19fe83ffb0c579.jpg', 'Flora', '', '', '', 100),
(79, 'Tenocté', 'Es un árbol característico de Comitán. Cuenta la leyenda que, cuando el tenocté florea, las muchachas preparan su “maletía” y se fugan con el novio. En la canción comiteca hay un verso que hace referencia a este árbol: “…Qué hermoso símbolo tienes en la flor del tenocté”', 'eabe7cb1bdbb606143e02b4388cf5afc.jpg', 'Flora', '', '', '', 100),
(80, 'Jacaranda', 'Árbol que avisa la llegada de la primavera y la Semana Santa. Contemplar a Comitán lleno de flores moradas que hacen contraste con el blanco del tenocté es algo único. La Iglesia Católica, en el interior de los templos, usa un color morado que identifica a la Semana Santa, un color que asemeja a las flores de la jacaranda.', '7d38f764f8bbbabff7dc142ad2e9f878.jpg', 'Flora', '', '', '', 100),
(81, 'Sabino', '(Sauce) Es un árbol que crece junto al agua. Sus hojas son elípticas, con ramas flexibles y largas. Con estos árboles se da nombre al barrio de Los Sabinos.', '5e68b6825cf3e1be29a804e2c71584d8.jpg', 'Flora', '', '', '', 100),
(82, 'Cedro', 'Árbol de madera fina y aromática que da nombre (en singular) al barrio de El Cedro, a diferencia de otros barrios que llevan su nombre en relación a santos o a emblemas de Comitán (San Sebastián, La Virgen de Guadalupe, La Pila). Este árbol ha sido una referencia muy importante para Comitán. Testigo del ir y venir de las generaciones. Ubicado en una de las entradas a Comitán.', '1bc736903d7e9c82610447e2b6890c44.jpg', 'Flora', '', '', '', 100),
(83, 'Chumish', 'Es un referente de Comitán. El 10 de febrero, todos los años, se convierte en punto de reunión para la entrada de flores en honor a San Caralampio. Se congrega mucha gente de diversas comunidades rurales, de las iglesias de Comitán, con el fin de llevar a cabo tan alegre fiesta, donde las comparsas se han convertido en una gran tradición.', '9e8f9ac096f8582fa507d5c8c49c13a6.jpg', 'Flora', '', '', '', 100),
(84, 'Chulul', 'Un punto de referencia  que ya no existe, pero que las personas mayores aún recuerdan. El árbol que estaba sembrado en la subida de Guadalupe. Donde actualmente se encuentra la lavandería el chulul. Y el árbol que se encontraba en el espacio que ahora ocupa el Centro de Salud. Este árbol estaba al lado del patio de los toros. El propietario lo alquilaba, para que la gente trepara al árbol y desde ahí pudiera ver las corridas de los toros. Pero llegaba el momento en que ya no entraba nadie más en el árbol. De ahí se deriva la frase “está cuajado el chulul”, que todavía se aplica para decir que hay una multitud, que ya no cabe más gente en un determinado espacio.', '457e34c70b1dcc7664211028d0de9764.jpg', 'Flora', '', '', '', 100),
(85, 'Correcaminos', 'Su hábitat son los espacios abiertos del chaparral y matorral semidesértico del norte de México y sudoeste de los Estados Unidos.\n\nAunque realiza pequeños vuelos de planeo desde árboles y arbustos hacia tierra, es un ave de costumbres terrestres. Es un magnífico corredor que puede alcanzar hasta los 25 km/hora utilizando su larga cola como estabilizador y como timón. Y es más, gusta de competir con aquellos animales o vehículos a los que ve correr. Tanto es así que el nombre les fue puesto por los primeros colonos al verlo correr por los caminos que sus carros habían abierto.\n\nPara sobrevivir utiliza su velocidad, ya que ésta le ayuda a escapar de sus enemigos y capturar a sus presas. Su rapidez le permite cazar a la peligrosa víbora de cascabel y aún más, atrapar al vuelo insectos y colibríes. También se alimenta de grano y pequeños reptiles, insectos y pequeños mamíferos.', '2feaf487f76309d00f9dc59bae9e3b77.jpg', 'Fauna', '', '', '', 100),
(86, 'Chachalaca', 'Existen cuatro especies de chachalaca en México, de las cuales dos son endémicas, es decir, se encuentran exclusivamente en territorio mexicano. Una de estas dos últimas especies, Ortalis policephala (nombre científico), se distribuye a lo largo de la costa del pacífico, desde el estado de Jalisco hasta Chiapas, llegando hasta el centro del país en los estados de México, Morelos y Puebla.\n \nDentro de sus hábitos de comportamiento, es curioso observar que el macho siempre ocupa la parte mas alta del lugar, su canto es más grueso que el de la hembra y además, comienza primero. La hembra se ubica más abajo en la vegetación, desde unos centímetros hasta tres metros de distancia del macho; su canto es mas suave e inicia uno o varios segundos después de su pareja.', 'fd4260f95e98c86b6b892cd26121c19b.jpg', 'Fauna', '', '', '', 100),
(87, 'Gavilán', 'El gavilán es una rapaz diurna de pequeño tamaño. Su cabeza es corta y pequeña, la hembra posee una lista ocular clara y las diferencias entre ambos sexos son bastante significativas. Las hembras son bastante más grandes, con las listas de color gris en el vientre y la parte dorsal de color azul. El macho tiene el vientre con dibujos de rayas horizontales de color anaranjado.', 'e0125ac69e2c38567b4e5495dbb61cc4.jpg', 'Flora', '', '', '', 100),
(88, 'Tapacaminos', 'Durante el día descansa en el suelo; en la noche, vuela a poca altura y caza insectos, que también come picoteando el suelo. En el verano, poco después del atardecer, comienza a repetir su canto toda la noche, que suena a “cu-ye-o”.\nEl tapacaminos es una de las pocas especies que se ha beneficiado de la deforestación limitada, pues crea áreas donde pueden cazar más eficientemente.  Sin embargo, abandona los lugares totalmente deforestados, pues los hace muy vulnerables a la caza de perros y gatos salvajes', '121bd4d7447a1033d661a19e4c7e79e7.jpg', 'Fauna', '', '', '', 100),
(89, 'Gorrión', 'Es un pajarito de color más bien desvaído, glotón y bochinchero, y tan confianzudo como para meterse en las habitaciones de las casas en busca de algún resto de comida. Y, además de todo, es “gringo”. Asociado al hombre desde el nacimiento de la agricultura, hace unos 12.000 años, en los valles de Eufrates y el Tigris, y debido a su gran capacidad de adaptación el gorrión fue ocupando todo el lugar habitado del planeta, desde las heladas tierras suecas con 45° bajo cero , hasta las muy cálidas del Mato Grosso, con temperaturas que sobrepasan los 55°; Y desde el nivel del mar hasta los 5.000 metros de altitud.', '4b61d7e5855852636e16ddb2112ca473.jpg', 'Fauna', '', '', '', 100),
(90, 'Conejo', 'Son fisicamente parecidas a las liebres pero de menor tamaño, y se caracterizan ademas por ser especies que se han habituado a ser animales domésticos, conviviendo con los seres humanos como mascotas.\nViven en madrigueras grandes y largas donde pueden llegar a caber hasta 10 ejemplares de conejos, que son muy territoriales. Pasan la mayor parte del día en las madrigueras ya que además tienen habitos mas bien nocturnos.\nSon animales silenciosos, pero si se encuentran heridos o amenazados emiten un sonido muy particular y fuerte.', '8893fbd3e238685dc63210758919a0ba.jpg', 'Fauna', '', '', '', 100),
(91, 'Comadreja', 'Es el representante más pequeño de los mamíferos carnívoros perteneciente a la familia de los mustélidos. Su cuerpo es pequeño, esbelto, alargado y flexible.La cabeza es aplanada, con el rostro muy corto, las orejas pequeñas y redondeadas, ojos y nariz oscuros y el cuello largo.', '384a368a5ce43730f473b7af2b2b38a9.jpg', 'Fauna', '', '', '', 100),
(92, 'Tacluache', 'El tlacuache es un animal omnívoro que no ha sufrido variaciones desde que apareció en nuestro planeta hace aproximadamente sesenta millones de años. Ha logrado preservar la especie y sobrevivir a la invasión humana.Se alimenta de cualquier cosa por lo que acostumbra estar la mayor parte del tiempo cerca del hombre.', 'd7adb4d146fbc05ec4a6486a8c13c621.jpg', 'Fauna', '', '', '', 100),
(93, 'Zorro gris', 'Unas de las especies más grandes de zorros son los zorros grises. Estos animales reciben ese nombre debido a los diferentes matices de colores que ofrecen. También tienen colores blancos y negros en sus cuerpos. El color negro se encuentra en todo su cuerpo, mientras que a menudo la coloración blanca se limita a la cara y a las zonas del vientre. Algunos de ellos tienen colores rojos cerca de los oídos también.', '0c419676af8c9bbe2e7cd698af3fcc27.jpg', 'Fauna', '', '', '', 100),
(94, 'Coyote', 'El coyote es un animal con gran capacidad de adaptación y prácticamente omnívoro. Caza conejos, roedores, peces, ranas e incluso venados, y tampoco le hace ascos a los insectos, las frutas, la hierba y la carroña. Muchos rancheros y agricultores le consideran una plaga destructiva, ya que a veces mata corderos, terneros y otras clases de ganado.', 'd41722a40f4004a08101aa7efb4314d7.jpg', 'Fauna', '', '', '', 100),
(95, 'Musaraña', 'Mamífero insectívoro, de aspecto y forma parecido al de un ratón casero, aunque se diferencia claramente del mismo por ser sus patas más cortas y particularmente por el característico y largo hocico cónico, provisto de vibrisas sensoriales, con el que está dotado la especie.', '0d2315e90b6c25a6023bb23d058cfebb.jpg', 'Fauna', '', '', '', 100),
(96, 'Ardilla', 'Las ardillas son animales pequeños, cuyo tamaño varía de una especie a otra. No obstante, la proporción media de estos mamíferos oscila entre 19 y 45 cm, y una significativa parte de su cuerpo está ocupada por la cola, que mide de 15 a 20 cm.', 'b40e0c21c26a638650575eff4815e264.jpg', 'Flora', '', '', '', 100),
(97, 'Armadillo', 'El armadillo es un mamífero acorazado, la armadura de este animal está constituida por un mosaico de pequeñas placas óseas que se desarrollan en la capa inferior de la piel o dermis, y están recubiertas de epidermis córnea. Constituye un sistema de protección contra los depredadores; en algunas especies está protegida incluso la cola. En todas las especies, excepto en el caso del armadillo gigante, las placas forman un escudo de una sola pieza sobre los hombros y otro sobre los cuartos traseros. La parte media del cuerpo del animal está recubierta por unas placas que forman bandas transversales articuladas entre sí, es decir, que están unidas por una piel blanda. Gracias a esto, algunas especies, como el armadillo de tres bandas, pueden enrollarse hasta formar una bola completamente acorazada y cubrir la única zona del cuerpo que se halla desprotegida: el abdomen.', '3f872cdb0c308ef2bf84c8dbf710cfcb.jpg', 'Fauna', '', '', '', 100),
(98, 'Avellano', 'Es un árbol que alcanza normalmente entre 3 a 8 m de alto, aunque en ocasiones puede llegar a los 15 m con una copa muy extendida de forma irregular, generalmente ramificado desde la base. De corteza marrón pálida o gris y profundamente estriada, su madera es dura, flexible y muy resistente.', '43ae26635e108c122e9bb5472611dfbc.JPG', 'Flora', '', '', '', 539),
(99, 'Encino', 'Quercus ilex o encina es un árbol de la familia de las fagáceas.  Es un árbol perennifolio nativo de la región mediterránea de talla mediana, aunque puede aparecer en forma arbustiva, condicionado por las características pluviométricas o por el terreno en el que se encuentre.', '5e32bb243dbc626c5d7192417db1544b.jpg', 'Flora', '', '', '', 539),
(100, 'Ocote', 'Esta conífera desprende una resina aromática que arde con facilidad cuando entra en contacto con el fuego debido a su resina inflamable. En algunos lugares su madera se utiliza para la construcción y también como combustible. ', '0ed221920c3b0a44674047b4945ec878.jpg', 'Flora', '', '', '', 539),
(101, 'Oyamel', 'Es un árbol de tamaño grande, perennifolio, de 25 a 50 m de altura, con un tronco recto de hasta 2 m de diámetro. Las hojas son como agujas, chatas, de 15 a 35 mm de longitud y 1,5 mm de ancho por 0,5 mm de espesor, verde oscuro en el haz, y con dos bandas azul blancas de estomas en el envés; el extremo de la hoja es agudo.', 'e3457249ae15a34d5ad5a02b3db847b5.jpg', 'Flora', '', '', '', 539),
(102, 'Ciprés', 'Como la gran mayoría de las coníferas, son de hoja perenne, pueden alcanzar los 20 m de altura con un diámetro aproximado de unos 60 cm. Su porte es piramidal, de crecimiento rápido en los primeros años de vida, ralentizándose después y pudiendo alcanzar los 300 años de vida.', 'eb73748050ca0d8beeee92a12e03899d.jpg', 'Flora', '', '', '', 539),
(103, 'Pino', 'Pinus es un género de plantas vasculares, comúnmente llamadas pinos, pertenecientes al grupo de las coníferas y, dentro de éste, a la familia de las pináceas, que presentan una ramificación frecuentemente verticilada y más o menos regular.\n\n', '1d71340ba9a4848bcbaf56069bd585a6.jpg', 'Flora', '', '', '', 539),
(104, 'Maíz', 'El maíz  es una especie de gramínea anual originaria de América e introducida en Europa en el siglo XVII. Actualmente, es el cereal con el mayor volumen de producción a nivel mundial, superando incluso al trigo y al arroz.', '98ef48e5b93c54cfdb966089885d4d2f.jpg', 'Flora', '', '', '', 539),
(105, 'Frijol', 'Es una especie anual nativa de Mesoamérica y Sudamérica, y sus numerosas variedades se cultivan en todo el mundo para el consumo, tanto de sus vainas verdes como de sus semillas frescas o secas. ', 'ebbd497a3e43e559b1fbe78f87f5fdea.jpg', 'Flora', '', '', '', 539),
(106, 'Manzano', 'El manzano, es un árbol de la familia de las rosáceas, cultivado por su fruto, apreciado como alimento. Domesticado hace más de 15 000 años, su origen parece ser el Cáucaso y las orillas del mar Caspio.', '843c3e31efb077e118364ffe76b93a58.png', 'Flora', '', '', '', 539),
(107, 'Perales', 'Los perales son un género de árboles frutales perteneciente a la familia de las rosáceas, la misma que el manzano. El género tiene alrededor de 30 especies con frutos carnosos. Su cultivo está muy extendido, principalmente para la obtención del fruto, llamado pera. ', '95c58a62dc80cc91811abf7f4f70654b.jpg', 'Flora', '', '', '', 539),
(108, 'Conejos', 'El conejo común o conejo europeo  es una especie de mamífero lagomorfo de la familia Leporidae, y el único miembro actual del género Oryctolagus.', 'ac39f44717f04b1b40dfc9de52c53ad6.jpg', 'Fauna', '', '', '', 539),
(109, 'Ardillas', 'Se llama ardilla a ciertas especies de roedores esciuromorfos de la familia Sciuridae.', 'e80f9c66a8663afac986726b01be7d1d.jpg', 'Fauna', '', '', '', 539),
(110, 'Liebre', 'Se conoce como liebre a varias especies de la familia Leporidae caracterizadas por la precocidad de las crías, que son capaces de correr y alimentarse por sí mismas desde el momento del nacimiento.', '238862f1787c16b9cbdf7ce17422a4c0.jpg', 'Fauna', '', '', '', 539),
(111, 'Roble', 'El roble común es árbol robusto que en espesura crece con tronco derecho y limpio sin ramificarse hasta los 15 metros. Cuando se halla aislado su copa se hace ancha irregular con ramas tortuosas, nudosas y acodadas que proporcionan escasa sombra.', '28cb6a68f764b5b885923b2b8e62fe9d.jpg', 'Flora', 'González Ortega 5, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.463453198845738', '-103.46099853515625', 171),
(112, 'Encino', 'Los encinos son árboles o arbustos que viven en las partes bajas de las montañas en donde forman bosques de varias especies de encinos o mezclados con varias especies de pinos.', '4f4cabab614a348d23c9bfc0de325e96.jpg', 'Flora', 'Francisco García Salinas 16-18, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.461695862802387', '-103.4602689743042', 171),
(113, 'Palo Colorado', 'Llega a tener 15 m de altura y su tronco unos 5 dm de diámetro, poseyendo una madera muy dura y una corteza lisa de color pardo rojizo, los tallos de sus ramas son rectos, sus hojas son ovaladas siempre verdes, las flores blanquecinas a modos de ramilletes se presentan en inflorescencias, sus frutos son redondos con cubierta de color marrón obscuro con tonalidades rojizas, éstos de sabor amargo, recuerdan a los del café, se les da principalmente un uso medicinal.', '058df657fb0443e6f6d2a41d7285a931.jpg', 'Flora', 'González Ortega 7, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.46329344189929', '-103.46099853515625', 171),
(114, 'Pino Real', 'Crece para después abrirse mediante ramas de similar grosor en una copa redondeada y achatada, en forma de sombrilla. La superficie del tronco se caracteriza por disponer de placas de color grisáceo, separadas por grietas rojizas. Prefiere los suelos arenosos.', 'c8d64d2f8b214b7b4a4c77e02a20cf78.jpg', 'Flora', 'González Ortega 13, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.46265441236212', '-103.46099853515625', 171),
(115, 'Venado Cola Blanca', 'Como el nombre de este animal indica, el venado de cola blanca tiene una mancha blanca que se encuentra alrededor de la zona de la cola. Son de color marrón claro con tonos rojizos, esto puede variar dependiendo de la edad y la ubicación. Lo interesante es que se sus cuerpos adquieren un color gris cuando el invierno se acerca y que dicha coloración está diseñada para permitirles mezclarse con el entorno tanto como sea posible.', '6fe9f9430636cbddf1d676eacce492f3.jpg', 'Fauna', 'Francisco García Salinas 28, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.46071733695721', '-103.46059083938599', 171),
(116, 'Coyote', 'El coyote mide menos de 60 cm de altura, y su color varía desde el gris hasta el canela, a veces con un tinte rojizo. Las orejas y el hocico del coyote parecen largos en relación al tamaño de su cabeza. Pesa entre 10 y 25 kg, promediando 15. Puede ser identificado por su cola espesa y ancha que, a menudo, lleva cerca del suelo. Por su aspecto esbelto se puede distinguir de su pariente mayor, el lobo gris, que puede pesar de 35 a 70 kg. El coyote es un animal muy flaco, y puede parecer desnutrido a primera vista aun si goza de buena salud.', 'dee92ea4afc064221d016f7c80dcc7cf.jpg', 'Fauna', 'Francisco García Salinas 44, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.45945922264566', '-103.46099853515625', 171),
(117, 'Viuda Negra', 'Las viudas negras no son agresivas y no tienen el instinto de morder; son tímidas, sedentarias, solitarias, caníbales y nocturnas. La única vida social que muestran es al aparearse. Reciben el nombre popular de viuda negra debido a que generalmente la hembra se come al macho después del apareamiento, aunque a veces el macho escapa y logra aparearse de nuevo; pero generalmente el macho se queda en la tela de la hembra para servirle de alimento y asegurar una buena puesta.', '233eac5d160d491f90bef9375834f9ad.JPG', 'Fauna', 'Francisco García Salinas 25B, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.46233489654271', '-103.45996856689453', 171),
(118, 'Coralillo', 'Se distinguen por tener la cabeza pequeña y el cuerpo delgado y alargado, pero más que nada, por una serie de anillos negros, amarillos y rojos característicos de todas las serpientes de coral; los anillos son de color brillante y avisan de su peligrosidad; estos anillos de colores se repiten a lo largo de su cuerpo en el mismo patrón, lo que las diferencia de las falsas serpientes de coral, que también tienen anillos negros, blancos y rojos.', 'f85e273532e784525f6d16ed6bfa981b.jpg', 'Fauna', 'González Ortega 6, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.462973927480967', '-103.46134185791016', 171),
(119, 'Garza Blanca', 'La garza blanca es un ave grande de plumaje blanco que puede alcanzar el metro de altura y pesar hasta 950 gramos. Además del tamaño, la garza blanca puede diferenciarse de otras garzas del mismo color por su pico amarillo y patas negras, aunque el pico puede tornarse más oscuro y las patas más claras en la estación de cría. En el plumaje de cría, delicadas plumas ornamentales salen de la espalda. Machos y hembras son idénticos en apariencia.', '0adb0235f0d3a15a09c8381a70806e64.jpg', 'Fauna', 'López Mateos 1B, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.459858625190957', '-103.4622859954834', 171),
(120, 'Cenzontle', 'Los ejemplares adultos son grises por la parte superior del cuerpo, con ojos de un amarillo pálido y pico negro curvo. La cola es de color oscuro con los bordes blancos, y las patas largas y negras. Las alas muestran finas líneas blancas, y durante el vuelo dejan ver manchas, también blancas en las alas y dos franjas blancas longitudinales en la cola. Se caracterizan por imitar sonidos de otros animales.', 'ddb9a48ccdb94feb248ed2c1d8103a26.jpg', 'Fauna', 'Francisco García Salinas 44, Téul de González Ortega, 99800 Teul de González Ortega, Zac., Mexico', '21.4593428', '-103.46090240000001', 171),
(121, 'Matorral Subtropical', 'Son grandes comunidades vegetales dominadas por arbustos de altura inferior a 4 m. Son propias de climas secos con lluvias escasas y zonas frágiles que favorecen la desertificación. Son el grupo más diverso de comunidades vegetales. Se les ve junto con otras especies como el cactos y agaves.', 'bdc9d8e9815fb3b72bf7c52441292377.jpg', 'Flora', 'Laguna de Yuriria', '', '', 547),
(122, 'Tule Typha domingensis', 'Es una planta acuática, herbácea, enraizada, emergente de hasta 2,5 m de altura. Sus hojas igualan o exceden la altura de las espigas, parte superior de la vaina hacia la lámina; asimétricas, con gran cantidad de glándulas mucilaginosas de color oscuro, dispuestas longitudinalmente y hacia la base de la lámina de 1,5 m de largo y de 8 a 13 mm de ancho, envés convexo cerca de la vaina. ', '17d4b734a33dfb61fad03c14adcff3ad.jpg', 'Flora', '', '', '', 547),
(123, 'Lirio Acuático', 'Son plantas flotantes y normalmente no superan los 50 cm. El cuerpo principal de la planta está formado por un rizoma estolonífero y ramificado, alcanzando los 30 cm de largo, del que nacen brotes auxiliares en ángulos variables. Los estolones son purpúreos, alcanzando los 50 cm de longitud. Las hojas están dispuestas semejando una roseta; son gruesas y redondas con el margen a veces ondulado.', 'f473439ab3662dce69ee9e0ae03335c2.jpg', 'Flora', '', '', '', 547),
(124, 'Conejo', 'Se caracteriza por tener un cuerpo cubierto de un pelaje espeso y lanudo, de color pardo pálido a gris, cabeza ovalada y ojos grandes. Pesa entre 1,5 y 2,5 kg en estado salvaje. Tiene orejas largas de hasta 7 cm las cuales le ayudan a regular la temperatura del cuerpo y una cola muy corta.', '3756ec59d91d9072ebb0a5214424f663.jpg', 'Fauna', '', '', '', 546),
(125, 'Pato Zambullidor Grande (Oxyura Jamaicensis)', 'El pato zambullidor grande (Oxyura jamaicensis), también conocido como malvasía canela, pato tepalcate (en México) o pato rufo, es una especie de ave anseriforme de la familia Anatidae natural de América.', '3df674d79ddf23b723fbfd94948c044d.jpg', 'Fauna', '', '', '', 547),
(126, 'Cerceta Azul Anas discors', 'Es pardo manchado y punteado de negro, con diseño alar como el del pato pico cuchara sudamericano, cabeza y cuello ceniciento oscuro, notable medialuna en la cara y mancha blanca en los flancos, en los machos. La diferencia entre machos y hembras es que las hembras no tienen la media luna en la cara, pero tiene una leve ceja loreal clara.', '55833d05bf518f1dbc31c164a56993a4.jpg', 'Fauna', '', '', '', 547),
(127, 'Tlacuache', 'Mamífero marsupial de hasta 50 cm de longitud, similar a la rata, hocico puntiagudo y cola prensil larga y desnuda que usa para trasladar a sus crías; es nocturno y omnívoro.', 'f86884793101cb66ab32f37cdae44407.png', 'Fauna', '', '', '', 546),
(128, 'Coyote', 'El coyote mide menos de 60 cm de altura, y su color varía desde el gris hasta el canela, a veces con un tinte rojizo. Las orejas y el hocico del coyote parecen largos en relación al tamaño de su cabeza. Pesa entre 10 y 25 kg, promediando 15. Puede ser identificado por su cola espesa y ancha que, a menudo, lleva cerca del suelo.', '851484a7c91ddf065c75a7996338446b.jpg', 'Fauna', '', '', '', 546),
(129, 'Anas Crecca', 'Los machos de esta especie de ave son fáciles de diferenciar ya que tienen la cabeza de color rojo y un antifaz verde. La hembra de esta ave es muy parecida a la del ánade real, plumaje marrón con líneas negras.', '3123d80c70607ffbd42641834ed6ef9e.jpg', 'Fauna', '', '', '', 547),
(130, 'Ardilla', 'Su cuerpo mide entre 20 y 30 cm y su cola entre 15 y 25 cm. Pesa de 250 a 340 g. Su pelaje es de color rojizo. Cuando llega el invierno aparecen unos penachos de pelos en las orejas.', '80683fb7af57482d04fb90fae9f5e3af.png', 'Fauna', '', '', '', 546),
(131, 'Conservación de Aves en la Laguna de Yuriria', 'La Laguna de Yuriria es reconocida como Área de Importancia para la Conservación de las Aves (AICAS) por la Comisión Nacional para el Conocimiento y Uso de la Biodiversidad  (CONABIO) en el año de 1999 y declara como Sitio RAMSAR en el año de 2004 por ser considerada como un Humedal de Importancia Internacional.', 'a47f6b605ee3b541054069d38b3f6ba5.jpg', 'UMA', '', '', '', 547),
(132, 'Zorrillo', 'Su tamaño es similar al de un gato doméstico; cuerpo rechoncho, cabeza chica, piernas cortas y cola larga y espesa; color negro con bandas blancas a cada uno de los lados, que llegan hasta la base de la cola. Mide 40 cm de cuerpo y otro tanto de la cola, con peso de 2 kg en promedio.', '6d87e6898316762a98252658eb79112d.jpg', 'Fauna', '', '', '', 546),
(133, 'Gavilan', 'Es un ave pequeña con alas largas y redondeadas y una cola larga que ayuda a desplazarse entre los árboles. La cola siempre es más larga que las alas, y en el macho adulto presenta unas cuatro a cinco franjas onduladas. Sus patas son largas y amarillas y el tarso no es más ancho que el diámetro del ojo.', '152c3aac72c1d4e599af42a6e4cae8ac.jpg', 'Fauna', '', '', '', 546),
(134, 'Maleza', 'La propiedad más universal de las malezas es su capacidad de colonizar, de\nprosperar, de competir y de persistir en un medio tan intensamente modificado\ncomo lo son los terrenos de cultivo y ambientes similares.', 'af47091e9e5a631d0c43aa1ebeb7e9e8.png', 'Flora', '', '', '', 546),
(135, 'Abrojillo', 'Planta herbácea de vida corta, hasta de 1 m de alto; tallo profusamente ramificado,\nprovisto de numerosas espinas amarillas hasta de 1 cm de largo; hojas de posición\nalterna, de forma aovada en contorno general, hasta de 13 cm de largo y 10 cm de ancho.', 'eb97a850f9adf65b4a70da0a56719a9e.png', 'Flora', '', '', '', 546),
(136, 'Aceitilla Blanca', 'Planta herbácea de vida corta, hasta de 1 m o a veces más de alto, erecta, de tallo\ncuadrangular y ramificado; hojas, al menos en su mayoría, de posición opuesta,\ncompuestas de 3 a 5 hojuelas simples, o bien éstas a su vez profundamente partidas;', 'f9047259bbed6fffe893c27e8fdc72b0.png', 'Flora', '', '', '', 546),
(137, 'Andán', 'Planta herbácea de vida corta, hasta de 2.5 m de alto, pero por lo general de talla\nmucho más modesta; tallo erecto o ascendente, por lo general ramificado; ', '1a61236d6f2b719e6a1d196cbaa11548.png', 'Flora', '', '', '', 546),
(138, 'Buena Moza', 'Planta arbustiva, de 1.5 a 6 m de alto; tallo poco ramificado; hojas de posición alterna,\nde forma aovada o elíptica, de 3 a 15 cm de largo y 1 a 8 cm de ancho, puntiagudas, de\nmargen entero o casi entero, de color verde tendiendo a azuloso; ', '8cc55e2ba1f72f3d5e6b3451ff425aa6.png', 'Flora', '', '', '', 546),
(139, 'Flor de Panal', 'Planta herbácea hasta de 70 cm de alto, provista de abundante líquido lechoso que\nmana de alguna incisión o lastimadura; hojas por lo común 4 a 6 partiendo al mismo\nnivel, de forma elíptica, a veces sumamente angosta, hasta de 13 cm de largo y 1.5 cm\nde ancho, puntiagudas, de margen entero.', 'e131b86c3188f5bedde9c92f4d5cefdc.png', 'Flora', '', '', '', 546);
