INSERT INTO `vpm_transporte` (`idTransporte`, `nombreTransporte`, `descripcionTransporte`, `imgTransporte`, `preciosTransporte`, `horariosTransporte`, `horariosExtra1`, `horariosExtra2`, `serviciosTransporte`, `direccionTransporte`, `latitudTransporte`, `longitudTransporte`, `idUsuario`) VALUES
(2, 'Autobuses Estrella Blanca', 'Grupo Estrella Blanca S.A. de C.V. nace en 1939 con la creación de "Transportes Nacionales del Centro Estrella Blanca, S.C.L.". Es una compañía dedicada a ofrecer servicios de transporte terrestre de pasajeros cubriendo las rutas de 27 estados del país, Estados Unidos y Canadá. Cuenta también con servicios de mensajería, paquetería y renta de autobuses.\n\nDirección: Avenida Gobernación calle Jorge Rojo Lugo S/N.\n', '3b4a2205d728a7e455302b1e461063e1.jpg', '', '', '', '0', '', 'Avenida Gobernación calle Jorge Rojo Lugo S/N', '', '', 10),
(3, 'Transportes Ramírez', 'Dirección: Javier Rojo Gómez 27, Barrio Abundio Gonzalez, 42400 Huichapan, Hgo. \n\nTeléfono: 01 761 782 0632', 'e1165482f69c81c70c4f9103845310fa.jpg', NULL, NULL, NULL, NULL, NULL, 'Javier Rojo Gómez 27, Barrio Abundio Gonzalez, 42400 Huichapan, Hgo', '20.3786677', '-99.65084009999998', 10),
(4, 'Sitio de Taxis', 'Dirección: Jardín Zaragoza S/N Centro Histórico.', '7364857488920c210d34b105ec7ca2ff.jpg', NULL, NULL, NULL, NULL, NULL, 'Jardín Zaragoza S/N Centro Histórico', '19.0155379', '-98.18112009999999', 10),
(5, 'Tequila Plus', 'Los autobuses de la línea ofrecen 38 corridas diarias que parten desde la Antigua Central Camionera de Guadalajara hacia el municipio; el recorrido tiene una duración aproximada de 1 hora con 50 minutos puesto que recoge pasaje en las poblaciones de la Venta, El Arenal y Amatitán, así como en la Terminal de Autobuses de Zapopan.', 'd9346a7e0ea9e30c33b085ef475be720.jpg', '85', '', NULL, NULL, '', '', '', '', 212),
(6, 'Terminal de Autobuses de Tequila', 'Emplazada en la Avenida Sixto Gorjón. El estacionamiento interior tiene capacidad para cinco autobuses; no cuenta con apeaderos y ofrece rutas de corto alcance, que en su mayoría parten hacia la capital tapatía. En general, el servicio que ofrecen ambas terminales es deficiente, razón por la cual, se hace necesaria la remodelación de ambos edificios o, en su caso, la conformación de una sola central de autobuses.', '0be56a52c9b1a749e375b6f264f3774d.jpg', '', '', NULL, NULL, '', '', '', '', 212),
(7, 'José Cuervo Express', 'En la actualidad, sólo el tren José Cuervo Express cubre la ruta Guadalajara-Tequila con recorridos diurnos y nocturnos de fin de semana. No obstante, este servicio ferroviario no es comúnmente utilizado como medio de transporte por la mayoría los turistas que desean visitar el Pueblo Mágico; más bien, se trata de una iniciativa privada que traslada pasajeros, cuyo interés principal radica en conocer la tradición de una de las casa tequileras más emblemáticas del municipio.', '39a4d1a9b66bf656ebf49b7e13829ba9.jpg', '', '', NULL, NULL, '', '', '', '', 212),
(8, 'Autobuses Vía', 'La línea de Autobuses Vía cuenta con andenes para sus unidades dispuestos en una terminal propia y sus corridas incluyen como destino final a la Ciudad de Puebla, vía Zacapoaxtla, Zaragoza, Libres, Oriental, El Carmen, San José Chiapa y Amozoc; esta ruta tiene 12 corridas diarias con una diferencia de una hora entre cada una.\nTiempo aprox. de recorrido 4 Hrs. también cuentan con algunas corridas por Autopista que acortan el tiempo de recorrido', '61f25ccf7ece9a8d440c9c40018b4764.jpg', '$190.00 mxn', '', NULL, NULL, '', 'Blvd. Norte #4222 Las Cuartillas 72050 Heróica Puebla de Zaragoza, Pue.', '', '', 235),
(9, 'Autobuses Grupo Texcoco-Plus', 'Salen corridas en diferentes horarios de la línea Grupo Texcoco-Plus con un tiempo de recorrido de 6 Hrs. aprox.', 'adc199b1ed08bb40cd68dfd9b4d96468.jpg', '', '', NULL, NULL, '', 'Calz. Ignacio Zaragoza 200 Venustiano Carranza 10 de Mayo 15290 Ciudad de México, D.F.', '19.430073', '-99.11231850000001', 235),
(10, 'Autobuses ADO', 'Teléfono: 01 998 877 0845\n', '5572d780084b4799203e994a0c74a966.jpg', '', '', '', '', '', 'Muelle de Ultramar, Zona Centro (ticket bus), Isla Mujeres, Quintana Roo', '21.2396958', '-86.7378655', 469),
(11, 'Primera Plus', 'Grupo Flecha Amarilla es una empresa líder dentro del sector autotransporte gracias a la confianza e inversiones, lo cual se ha traducido en un crecimiento paulatino pero sostenido en la diversidad de servicios que cubren de manera íntegra varios segmentos de mercado entre los que podemos citar, de lujo, primera clase, regular o económico y alimentador.', 'dc6faeb3b99b2bf2c7cc0969cf866a05.jpg', '300.00- En adelante dependiendo origen.', '24 horas', '', '', 'Transporte\nBaño\nTelevisión\nWi-Fi', 'Blvd. Sierra Gorda No. 115 Int. Central de Autobuses', '21.296178', '-100.525592', 35),
(12, 'Transportes Chihuahuenses', 'La mejor empresa de autotransporte, Transportes Chihuahuenses, le ofrece un servicio de primera en todos sus viajes. Te unimos a lo que más quieres.', 'dd2d89bb4fd152b9f89b916bd3778478.jpg', '$329.00 - En adelante dependiendo origen.', '24 horas', '', '', 'Transporte\nCambiador de Pañales\nBaños Separados\nEnchufes para laptop y celulares\nWi-Fi\n', 'Ubicación: Blvd. Sierra Gorda No. 115, San Luis de La Paz, Guanajuato.', '21.296178', '-100.525592', 35),
(13, 'Costa Line AERS.', 'Venta de Boletos:\nD.F. y Área Metropolitana: \n53365560\n018000037635', '4f2b241fa70dcc7719be21acf8e976a6.jpg', '185.00', '16:05', '17:05', '18:05', 'Transporte de personas a Taxco de Alarcón, Guerrero.', 'Calle Juan Álvarez Sur # 93 Col. Centro Atoyac, CP.400030.', '', '', 76),
(14, 'Autotransportes de Oriente', 'Autobuses de Oriente, S.A. de C.V., es una empresa dedicada al transporte de pasajeros en México. Es la línea bandera del Grupo ADO.', '1efb5845b6779f9e8bd22aa32db870e9.jpg', '', '', '', '', '', '', '', '', 539),
(15, 'Autotransportes Ejecutivos del Valle', 'En  Autotransportes Ejecutivos del Valle estamos comprometidos para otorgarle la más alta calidad en los servicios de renta de vehículos y transportación de personal. Tenemos experiencia y una larga trayectoria, lo que nos convierte en una excelente opción para su empresa.', '39084f88943cc1e7b6707e7d7cd2a17d.jpg', '', '', '', '', '', '', '', '', 539),
(16, 'Turísticos Guerrero', '', '3bef2ebeb1c2f6efbc5f97689e55308f.jpg', '', '', '', '', '', 'Carr. México-Pachuca Km. 87.8 Local 5 Isla G Plaza Perisur', '', '', 539),
(17, 'EL CHEPE (UNA AVENTURA EN TREN) ', 'Con más de 50 años de servicio para los residentes de Chihuahua y sus visitantes, el Chihuahua-Pacífico, mejor conocido como El Chepe, es el único tren de pasajeros en México. Conecta los estados de Chihuahua y Sinaloa y proporciona una experiencia inolvidable. Viajar en él permite admirar paisajes espectaculares y encontrarte con la cultura rarámuri, una de las más antiguas y mejor conservadas en México. Este tren recorre una distancia de 652 kilómetros dos veces por día, pasando a través de la Sierra Tarahumara y deteniéndose en las nueve principales atracciones turísticas de Barrancas del Cobre. ', '81595294408b6f0b8f62396fe74d5081.jpg', 'Desde $318.57 hasta $1,564.09', 'Puedes abordar El Chepe a las seis de la mañana todos los días del año. ', '', '', 'A lo largo del viaje de El Chepe disfrutarás cómodos asientos, un comedor con servicio completo y comida rápida, un bar, aire acondicionado, calefacción y baños ecológicos. \n', '', '', '', 113),
(18, 'Omnibus de México', 'En Omnibus de México trabajamos para satisfacer los requisitos y las expectativas de los clientes.', 'a1a4467c1d7db4b666837397a126f79b.jpg', '245.00', '0:00 a 23:30', '', '', '', 'Transito Pesado, S/N,Fraccionamiento Lomas De Isabelica,98099 Zacatecas, Zac.', '22.755554', '-102.576086', 171),
(19, 'Zina-bus', 'Nacional 16 A, Col. Centro\nC.P. 61410', '1895e9519aeda9ee0f43b36fbe92f300.jpg', '', ' Lunes a Domingo 5:30 am - 17:00 pm', '', '', '41 confortables asientos\nTipo Autobus: Dina F11 y Oisa Halcon\nConductores Altamente Capacitados\nServicio Ordinario', '', '', '', 98),
(20, 'TRANSPORTES ATINOCH', 'Alquilamos Autobuses. Transportes Atinoch. Alquilamos Camioneta. Microbuses y Automoviles. Autobuses. Autobuses para Turismo.', '81300c5fd1b138d939fad71fd6bd0189.jpg', '', '', '', '', '"TRANSPORTES ATINOCH\r\nAUTOBUSES\r\nMICROBUSES Y AUTOMOVILES\r\nAUTOBUSES PARA TURISMO\r\nALQUILER DE CAMIONETA\r\nALQUILER DE AUTOBUSES\r\nEXCURSIONES Y VIAJES ESPECIALES\r\nA TODA LA REPUBLICA\r\nAUDIO\r\nSEGURO VIAJERO\r\nTV\r\nVIDEO\r\nCAFETERIA\r\nCLIMA\r\nWC"', 'SANTOS DEGOLLADO. 51, COL. CENTRO, COATEPEC, C.P. 91500,VER.', '19.4534104', '-96.963394', 25),
(21, 'Permisionario del Centro', '', '', '', '', NULL, NULL, '', 'Av de la Convención de 1914 Sur 405, Las Américas, 20230 Aguascalientes, Ags., México', '21.866039186363345', '-102.29891538619995', 740),
(22, 'Central de Autobuses Adalberto Rodriguez Uribe', 'Autobuses Conexión de Estrella Blanca ofrece servicio a los siguientes destinos: Central de Autobuses de Norte en la Ciudad de México, Querétaro, San Juan del Río, Nopala de Villagrán, Huichapan, Tecozautla, Ixmiquilpan y Pachuca de Soto. ', 'DSC03825.4718e1.jpg', '', '', NULL, NULL, '', '', '', '', 66),
(23, 'Base de Taxis Huichapan', 'Centro Histórico: Jardín Zaragoza, Benito Juárez, Central de Autobuses Adalberto Rodríguez Uribe, Plazuela José Lugo Guerrero y Parque Pedro María Anaya. ', '5fd80c12-b945-49ee-94ff-3777af52fb14.40ed44.jpg', '', '', NULL, NULL, '', 'Hidalgo San José Atlán-Las Rosas 6, Centro Histórico, 42400 Huichapan, Hgo., México', '20.374868033066008', '-99.65082138776779', 66);
